mapper.delete:
delete from mnt_install_online_info
where online_id in

    #{item}

mapper.update:
update mnt_install_online_info
set rel_id = #{relId}
where online_id = #{onlineId}

mapper.select:
select

from mnt_install_online_info


        online_id = #{onlineId}


        and base_id = #{baseId}


        and prod_id = #{prodId}


        and ver_code = #{verCode}


        and rel_id = #{relId}


        and rel_dtl_id = #{relDtlId}


        and module_id = #{moduleId}


        and is_onsite_support = #{isOnsiteSupport}


        and is_remote_support = #{isRemoteSupport}


        and plan_online_date = #{planOnlineDate}


        and online_date = #{onlineDate}


        is_fault = #{isFault}


        and is_onlined = #{isOnlined}


        and un_online_reason = #{unOnlineReason}


        and delay_online_date = #{delayOnlineDate}


        and online_remark = #{onlineRemark}


        and delete_flag = #{deleteFlag}


        and creator = #{creator}


        and create_date = #{createDate}


        and modifier = #{modifier}


        and modify_date = #{modifyDate}

select
,
dtl_code,
dtl_name,
dtl_desc
from 
mnt_install_online_info t1 join mnt_release_rec_dtl t2
on t1.rel_dtl_id = t2.dtl_id
join mnt_release_rec t3 on t2.rel_id = t3.rel_id


        and online_id = #{onlineId}


        and t1.base_id = #{baseId}


        and t1.prod_id = #{prodId}


        and t1.ver_code = #{verCode}


        and t1.rel_id = #{relId}


        and t3.rel_code like '%${relCode}%'


        and t2.dtl_code like '%${mntReleaseRecDtl.dtlCode}%'


        and plan_online_date = #{planOnlineDate}


        and (plan_online_date = #{onlineDate} or online_date = #{onlineDate} or delay_online_date = #{onlineDate})


        and is_fault = #{isFault}


        and is_onlined = #{isOnlined}


        and ((plan_online_date >= #{beginDate} and plan_online_date <= #{endDate}) 
        or (online_date >= #{beginDate} and online_date <= #{endDate})
        or (delay_online_date >= #{beginDate} and delay_online_date <= #{endDate}))



        and t1.delete_flag = #{deleteFlag}

select
,
dtl_code,
dtl_name,
dtl_desc
from 
mnt_install_online_info t1 join mnt_release_rec_dtl t2
on t1.rel_dtl_id = t2.dtl_id

	is_onlined = 0

       and online_id = #{onlineId}


        and t1.base_id = #{baseId}


        and t1.prod_id = #{prodId}


        and t1.ver_code = #{verCode}


        and ((plan_online_date >= #{beginDate} and plan_online_date <= #{endDate}) 
        or (online_date >= #{beginDate} and online_date <= #{endDate})
        or (delay_online_date >= #{beginDate} and delay_online_date <= #{endDate}))



        and t1.rel_id = #{relId}


        and plan_online_date = #{planOnlineDate}


        and (plan_online_date = #{onlineDate} or online_date = #{onlineDate} or delay_online_date = #{onlineDate})


        and is_fault = #{isFault}



        and t1.delete_flag = #{deleteFlag}
